/* main.js */function renderRatings(){$("#bg .rating").each(function(){var e=$(this).attr("id"),t=e in rdata?Math.ceil(rdata[e].score/rdata[e].votes):1,n=e in rdata?rdata[e].votes:0;if(e in ratings)ratings[e].reset();else{var r=new jVote(e,{max:5,min:t,click:doStuff,labels:[1,2,3,4,5]});ratings[e]=r}$(".votes span",$(this).parent()).text(n);$(this).parent().show()})}function doStuff(e,t){var n=$(e.target).closest(".rating"),r=$(n).attr("id");$.post("ratings/rating.php",{id:r,score:t},function(e){rdata=jQuery.parseJSON(e);renderRatings()});ratings[r].lock()}var ratings={},rdata;$(document).ready(function(){$("#client-slides").slidesjs({width:304,height:197,navigation:{active:!1},pagination:{active:!1},play:{active:!1,effect:"fade",interval:5e3,auto:!0},effect:{fade:{speed:500}}});$("#top-slides").slidesjs({width:775,height:220,navigation:{active:!1},pagination:{active:!1},play:{active:!1,effect:"fade",interval:5e3,auto:!0},effect:{fade:{speed:500}}});$(".side-form").validate({rules:{"client-name":"required","client-phone":"required","client-name":"required","client-deadline":"required","client-email":{required:!0,email:!0}}});$("#client-deadline").datepicker({showOn:"both",buttonImage:"../img/calendar.gif",buttonImageOnly:!0});$.getJSON("ratings/rating.php",function(e){rdata=e;renderRatings()})});